import "./App.css";
import React, { Component } from "react";

export class App extends Component {
  constructor() {
    super();
    this.state = {
      fullName: "Achref belhay",
      bio: "studying in go my code",
      profession: "Full stack Web developper",

      show: true,
      count: 0,
    };
  }
  handleShowPerson = () => {
    this.setState({
      ...this.state,
      show: !this.state.show,
    });
  };
  handleIncrement = () => {
    this.setState({ count: this.state.count + 1 });
  };

  render() {
    if (this.state.show)
      return (
        <div className="App">
          <p>
            Hello my name is {this.state.fullName} I'm in high spirits
            {this.state.bio} I'm a {this.state.profession} .
          </p>
          <img src="/John-Doe.jpg" alt="it me " /> <br />
          <button onClick={this.handleShowPerson}>
            {this.state.show ? "click me" : ""}
          </button>
          <button onClick={this.handleIncrement}>Counter</button>
          <span>{this.state.count}</span>
        </div>
      );
    else
      return (
        <div className="App">
          <h1>Slm chab√©b</h1>
          <button onClick={this.handleShowPerson}>
            {this.state.show ? "" : "Hello"}
          </button>
        </div>
      );
  }
}

export default App;
