import "./App.css";
import React, { Component } from "react";

export class App extends Component {
  handleShowPerson = () => {
    this.setState({
      ...this.state,
      show: !this.state.show,
    });
  };
  constructor() {
    super();
    this.state = {
      fullName: "Achref belhay",
      bio: "studying in go my code",
      profession: "Full stack Web developper",
    };
  }
  show: true;
  render() {
    return (
      <div className="App">
        <p>
          Hello my name is {this.state.fullName} I'm in high spirits
          {this.state.bio} I'm a {this.state.profession} .
        </p>
        <img src="/John-Doe.jpg" alt="it me " /> <br />
        <button onClick={() => this.state.handleShowPerson()}>Click Me</button>
      </div>
    );
  }
}

export default App;
